DROP TABLE IF EXISTS `y_admin_menu`;
CREATE TABLE `y_admin_menu` (
  `id` smallint(6) unsigned NOT NULL AUTO_INCREMENT,
  `title` char(40) NOT NULL DEFAULT '' COMMENT '菜单名称',
  `parentid` smallint(6) NOT NULL DEFAULT '0' COMMENT '父ID',
  `module` char(20) NOT NULL DEFAULT '' COMMENT '模块',
  `action` char(20) NOT NULL DEFAULT '' COMMENT '方法',
  `data` char(100) NOT NULL DEFAULT '' COMMENT '参数',
  `listorder` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `display` enum('1','0') NOT NULL DEFAULT '1' COMMENT '是否显示',
  `safe` tinyint(1) NOT NULL DEFAULT '0' COMMENT '安全菜单',
  `settings` text COMMENT '扩展字段',
  PRIMARY KEY (`id`),
  KEY `listorder` (`listorder`),
  KEY `parentid` (`parentid`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=82 DEFAULT CHARSET=utf8 COMMENT='菜单表';

