-- ----------------------------
-- Table structure for `y_album_content`
-- ----------------------------
DROP TABLE IF EXISTS `y_album_content`;
CREATE TABLE `y_album_content` (
  `album_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '图集id',
  `album_list` text NOT NULL COMMENT '组图',
  `introduce` text NOT NULL COMMENT '摘要',
  `content` mediumtext NOT NULL COMMENT '内容',
  `seo_title` varchar(255) NOT NULL DEFAULT '' COMMENT 'SEO标题',
  `seo_keywords` varchar(255) NOT NULL DEFAULT '' COMMENT 'SEO关键字',
  `seo_description` text NOT NULL COMMENT 'SEO描述',
  PRIMARY KEY (`album_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='图集内容表';

-- ----------------------------
-- Records of y_album_content
-- ----------------------------
INSERT INTO `y_album_content` VALUES ('1', '', '当一个人静坐,默默的感受心里那份惆怅的时候，想念就像清风如影随行。想一个人会多么寂寞，念一个人会多么心痛，想念一个人的夜会多么寒冷。挥不去的，是袅袅缠绕的眷恋；斩不断的，是缕缕交织的思念；转不出的，是汩汩而逝的流年。我和你，一步之遥，我既无法上前一步，陪伴你左右；也无法退后一步，重新找回朋友的支点，只能静静地看着你，默默地祝福你。我不去想是否能够找到更好的，...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/55845/5667c2ad0da4a_28\"><img src=\"http://p2.wmpic.me/article/2015/12/09/1449640621_npUhvkpC.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">当一个人静坐,默默的感受心里那份惆怅的时候，想念就像清风如影随行。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/55845/5667c2ad33ab1_73\"><img src=\"http://p2.wmpic.me/article/2015/12/09/1449640621_IQWtcclF.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">想一个人会多么寂寞，念一个人会多么心痛，想念一个人的夜会多么寒冷。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/55845/5667c2ad39f20_52\"><img src=\"http://p2.wmpic.me/article/2015/12/09/1449640621_ovhCvVHU.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">挥不去的，是袅袅缠绕的眷恋；斩不断的，是缕缕交织的思念；转不出的，是汩汩而逝的流年。</span><a href=\"http://www.wmpic.me/55845/5667c2ad48bbc_18\"><img src=\"http://p2.wmpic.me/article/2015/12/09/1449640621_pPwfEeJF.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">我和你，一步之遥，我既无法上前一步，陪伴你左右；</span><a href=\"http://www.wmpic.me/55845/5667c2ad4f339_34\"><img src=\"http://p2.wmpic.me/article/2015/12/09/1449640621_hIVInecj.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">也无法退后一步，重新找回朋友的支点，只能静静地看着你，默默地祝福你。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/55845/5667c2ad55d44_51\"><img src=\"http://p2.wmpic.me/article/2015/12/09/1449640621_SFgXTlAO.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">我不去想是否能够找到更好的，既然选择了离开，便只顾寻觅前途绚丽的彩虹；</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">我不去想，身后会不会袭来寒风冷雨，</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/55845/5667c2ad5cd63_82\"><img src=\"http://p2.wmpic.me/article/2015/12/09/1449640621_bzCPBCTI.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">既然目标是黎明的地平线，留给你的只能是渐行渐远的背影。</span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">很多时候，跟自己过不去的，是我们自己。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/55845/5667c2ad63034_72\"><img src=\"http://p2.wmpic.me/article/2015/12/09/1449640621_SNUJSwsb.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">旅途中，背着一个沉重的包袱的人很难有心情观看沿途的风景，更会落后于别人的步伐。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('2', '', '曾经有人说：爱情是一场长跑，看你是否能坚持到终点，可是我一直在跑，却忘记了终点在哪儿丢掉了哪个曾要伴我跑下去的人。我不知道时候开始一路奔波只是剩下了我一个人的身影，而身后早已被时光磨灭。曾经有人说：爱情最终会被牛奶面包打败。是社会太物质还是我们太天真。曾经我一直相信爱情的神圣和纯洁，相信他的力量，可是最终却活在自己的世界里，败给了天真的自己。', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/55890/5669142d4f16c_16\"><img src=\"http://p2.wmpic.me/article/2015/12/10/1449727021_nWDRnJlR.jpg\" alt=\"\" width=\"500\" height=\"340\" style=\"width: 500px; height: 340px;\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">曾经有人说：</span>爱情<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">是一场长跑，看你是否能坚持到终点，可是我一直在跑，</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">却忘记了终点在哪儿</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/55890/5669142d622ec_50\"><img src=\"http://p2.wmpic.me/article/2015/12/10/1449727021_XOyBfWOW.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">丢掉了哪个曾要伴我跑下去的人。我不知道时候开始一路奔波只是剩下了我</span>一</p><p style=\"text-align: center;\">个人<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">的身影，而身后早已被时光磨灭。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/55890/5669142d70f72_61\"><img src=\"http://p2.wmpic.me/article/2015/12/10/1449727021_bpIXkkDh.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">曾经有人说：爱情最终会被牛奶面包打败。是社会太物质还是我们太天真。</span><a href=\"http://www.wmpic.me/55890/5669142d77c8c_25\"><img src=\"http://p2.wmpic.me/article/2015/12/10/1449727021_mkFsihgi.jpg\" alt=\"\" width=\"500\" height=\"370\" style=\"width: 500px; height: 370px;\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">曾经我一直相信爱情的神圣和纯洁，相信他的力量，可是最终却活在自己的世</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">界里，败给了天真的自己。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('3', '', '有些感情都已经深深的藏在心里，刻在心里最隐蔽的角落从不对人提及，彼此默默地念着那个美好而又禁忌的名字，静静的思念。当很多年过去，若爱再度重逢时，一切都没有发生改变，少年还是那个少年少女还是那个少女，彼此一直在那里，又未曾走远，我不知道是该笑还是该哭或是怪命运太会捉弄人。岁月拆散了时间却又给了彼此重逢的流年，那谁，你会不会也认为这是命运的奇特安排或者说是缘分给...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/55934/566a5ca17266f_22\"><img src=\"http://p2.wmpic.me/article/2015/12/11/1449811105_kbJifINs.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">有些</span>感情<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">都已经深深的藏在心里，刻在心里最隐蔽的角落从不对人提及，彼此默默地念着那个</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">美好而又禁忌的名字，静静的思念。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/55934/566a5ca18074a_91\"><img src=\"http://p2.wmpic.me/article/2015/12/11/1449811105_ymXGxQsL.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">当很多年过去，若爱再度重逢时，一切都没有发生改变，少年还是那个少年</span><a href=\"http://www.wmpic.me/55934/566a5ca189f68_48\"><img src=\"http://p2.wmpic.me/article/2015/12/11/1449811105_KNRIyacX.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">少女还是那个少女，彼此一直在那里，又未曾走远，我不知道是该笑还是该哭</span><a href=\"http://www.wmpic.me/55934/566a5ca18fb35_92\"><img src=\"http://p2.wmpic.me/article/2015/12/11/1449811105_kDoPgDGm.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">或是怪命运太会捉弄人。岁月拆散了时间却又给了彼此重逢的流年，那谁，你会不会也认为这</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">是命运的奇特安排</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/55934/566a5ca1962d2_31\"><img src=\"http://p2.wmpic.me/article/2015/12/11/1449811105_uSHODhkc.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">或者说是缘分给予我们奇妙的再度想见？我不知道需要多久的坚持和等待才遇到这么一个一生注定的人</span><a href=\"http://www.wmpic.me/55934/566a5ca19d2d7_65\"><img src=\"http://p2.wmpic.me/article/2015/12/11/1449811105_OTqHkXys.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">或许是一辈子，或许是一辈子也遇不见，但是我相信，既然遇见，那便是有缘，或者用你的话</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">说那叫命中注定一生所盼。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('4', '', '我向她点头轻笑道一句在下清潋，她则因我是生客顿时小晕潮红，绣帕后的一双美目顾盼生辉这时慕容先生才道这是息女若兰，让公子见笑了，我则说不然看令爱柔桡轻曼，秀外慧中定有咏絮叹机之才掌中飞舞之艺，不可凌掩！慕容先生一面说过奖了一面引我到客房敬东道之仪，酒足饭饱后慕容先生让我早点休息时天色已黑我也因连日的奔波筋疲力尽，刚倒头就要睡觉突然一缕琴声入耳，初时低沉古朴在爱...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/55976/566b8e5bd9eac_49\"><img src=\"http://p1.wmpic.me/article/2015/12/12/1449889371_WkkRumdy.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">我向她点头轻笑道一句在下清潋，她则因我是生客顿时小晕潮红，绣帕后的一双美目顾盼生辉</span><a href=\"http://www.wmpic.me/55976/566b8e5bec9f4_53\"><img src=\"http://p1.wmpic.me/article/2015/12/12/1449889371_vdEwUOtf.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">这时慕容先生才道这是息女若兰，让公子见笑了，我则说不然看令爱柔桡轻曼，秀外慧中定有咏絮叹机之才</span><a href=\"http://www.wmpic.me/55976/566b8e5c0f8aa_69\"><img src=\"http://p2.wmpic.me/article/2015/12/12/1449889372_bsJcjnmy.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">掌中飞舞之艺，不可凌掩！慕容先生一面说过奖了一面引我到客房敬东道之仪，酒足饭饱后慕容先生让我早点休息</span><a href=\"http://www.wmpic.me/55976/566b8e5c16f61_50\"><img src=\"http://p2.wmpic.me/article/2015/12/12/1449889372_DTlUczBg.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">时天色已黑我也因连日的奔波筋疲力尽，刚倒头就要睡觉突然一缕琴声入耳，初时低沉古朴</span><a href=\"http://www.wmpic.me/55976/566b8e5c1e2ab_28\"><img src=\"http://p2.wmpic.me/article/2015/12/12/1449889372_WRxJdrEa.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">在爱的旅途中，我们每</span>一个人<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">都在努力寻找着属于自己的方向，一旦认定了，然后拼尽全力为了心中的远方踏</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">上追逐爱的终点，希望有一天可以遇见一个人，抵达爱的彼岸，修成正果。</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\"></span><a href=\"http://www.wmpic.me/55976/566b8e5c24d72_69\"><img src=\"http://p2.wmpic.me/article/2015/12/12/1449889372_eGcDcbDf.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\">爱情<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">的最初开始总是卑微的，卑微到不敢靠近喜欢的人一步，卑微到掉进孤独的尘埃里</span><a href=\"http://www.wmpic.me/55976/566b8e5c2c6ee_84\"><img src=\"http://p2.wmpic.me/article/2015/12/12/1449889372_IZLgLtza.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">最后，要么在卑微的尘埃里，开出灿烂的花朵；要么在尘埃的卑微里，成为另一颗不起眼的尘埃。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('5', '', '你其实就是《关雎》里走来的那个人，因为，你的脸庞，就是我曾经看见过的那个临水照花的姑娘！如若，终于有一天，与你在不经意的街巷里行走时，碰面，擦肩，你回头，我转身互相对望，也无言语，不知彼此心下该当如何，--是羞涩，在你心里开放成青丝绸帕上的暗莲花，亦或，还是欢喜，在我心头荡漾作熟宣素简上的工整墨迹？冬的空气澄澈，冬的心事清澜，你不管冬月是不是在呢喃，也不管冬雨...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56022/566e37fd1fd59_52\"><img src=\"http://p1.wmpic.me/article/2015/12/14/1450063869_iPaNMdMi.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">你其实就是《关雎》里走来的那个人，因为，你的脸庞，就是我曾经看见过的那个临水照花的姑娘！</span><a href=\"http://www.wmpic.me/56022/566e37fd38913_95\"><img src=\"http://p1.wmpic.me/article/2015/12/14/1450063869_oLpVjNTA.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">如若，终于有一天，与你在不经意的街巷里行走时，碰面，擦肩，你回头，我转身</span><a href=\"http://www.wmpic.me/56022/566e37fd3f7d0_85\"><img src=\"http://p1.wmpic.me/article/2015/12/14/1450063869_OWXdZCVU.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">互相对望，也无言语，不知彼此心下该当如何，--是羞涩，在你心里开放成青丝绸帕上的暗莲</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">花，亦或，还是欢喜，在我心头荡漾作熟宣素简上的工整墨迹？</span><a href=\"http://www.wmpic.me/56022/566e37fd45bbb_64\"><img src=\"http://p1.wmpic.me/article/2015/12/14/1450063869_dHxUdtnG.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">冬的空气澄澈，冬的心事清澜，你不管冬月是不是在呢喃，也不管冬雨是不是在洒落，</span><a href=\"http://www.wmpic.me/56022/566e37fdb14be_61\"><img src=\"http://p1.wmpic.me/article/2015/12/14/1450063869_gYEnJsdA.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">还是冬雪在风里如何的写意，其实，终是已经藏在了黄公望皴染的水墨山水里。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('6', '', '安静时，总有一份思念在身体里蔓延；无助时，总有一份温暖在指尖漫溢，爱在思念的两端，情在时光的两岸，用情相守，以心相连，遥寄心语，那份惦念，一如往昔。今年我们这儿的气候特怪，仿佛从夏季直接就进入了冬天接连几天的北风呼啸，街上的行人都穿上了厚厚的羽绒服，虽已寒风刺骨，但我的心却如那正午的骄阳照在心房让人暖暖的，暖暖的，丝毫感觉不到冬季的寒意，只因你的回眸，这个冬...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56067/566f86b543382_21\"><img src=\"http://p3.wmpic.me/article/2015/12/15/1450149557_fwYnERCW.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">安静时，总有一份思念在身体里蔓延；无助时，总有一份温暖在指尖漫溢，爱在思念的两端，情在时光的两岸，用</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">情相守，以心相连，遥寄心语，那份惦念，一如往昔。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56067/566f86b55ce39_75\"><img src=\"http://p3.wmpic.me/article/2015/12/15/1450149557_sBcQUCvM.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">今年我们这儿的气候特怪，仿佛从夏季直接就进入了冬天</span><a href=\"http://www.wmpic.me/56067/566f86b563b35_48\"><img src=\"http://p3.wmpic.me/article/2015/12/15/1450149557_UaGXGnNA.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">接连几天的北风呼啸，街上的行人都穿上了厚厚的羽绒服，虽已寒风刺骨，但我的心却如那正午的骄阳照在心房</span><a href=\"http://www.wmpic.me/56067/566f86b56c1ba_67\"><img src=\"http://p3.wmpic.me/article/2015/12/15/1450149557_aWGyRHIf.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">让人暖暖的，暖暖的，丝毫感觉不到冬季的寒意，只因你的回眸，这个冬季多了一份关怀，多了一份体贴</span><a href=\"http://www.wmpic.me/56067/566f86b57307a_73\"><img src=\"http://p3.wmpic.me/article/2015/12/15/1450149557_PCSlcJut.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">让人暖心，让人陶醉，让人乐不思蜀。对你的思念，让心有千千闕歌，日日耳边余音缭绕</span><a href=\"http://www.wmpic.me/56067/566f86b578df8_91\"><img src=\"http://p3.wmpic.me/article/2015/12/15/1450149557_BisucJjQ.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">忧着，伤着却又暗香浮动着；愁着，痛着却又独自狂喜。雨，下了一整夜，而我，却拥抱了</span>太阳<a href=\"http://www.wmpic.me/56067/566f86b580893_91\"><img src=\"http://p3.wmpic.me/article/2015/12/15/1450149557_HXhIvwfP.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">如果，来世还有爱，我愿随一树飘零，追逐你最后的身影，从此，贴近你的胸膛，靠近你的温暖，你总是埋怨我为</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">什么不放弃，如果能我还会低眉浅笑的恋着你吗？</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('7', '', '爱如胭红，佳期如梦，谁在箜篌里飞出，落于筝音的弦脉，躁动了悲情的灵魂去迎接天地的流浪，千年的客驿，千年的等待，又千年的寻觅，终了于情，末了于情垂泪了西厢月下，默守了楼台一诺，这是谁人的谁的世界，又是谁的谁人的红颜！我不说，你又何必相问，你要说，又何必是挥手告别，看花曾与楚王醉，听琴又把张生恨哪般，这般，是错过还是过错。游园惊梦，牡丹亭下，折柳问飞花，面对春光...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56112/5670d70341724_66\"><img src=\"http://p3.wmpic.me/article/2015/12/16/1450235651_YvVFmWux.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">爱如胭红，佳期如梦，谁在箜篌里飞出，落于筝音的弦脉，躁动了悲情的灵魂</span><a href=\"http://www.wmpic.me/56112/5670d7034756f_35\"><img src=\"http://p3.wmpic.me/article/2015/12/16/1450235651_MGcTcAOr.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">去迎接天地的流浪，千年的客驿，千年的等待，又千年的寻觅，终了于情，末了于情</span><a href=\"http://www.wmpic.me/56112/5670d703504f7_35\"><img src=\"http://p3.wmpic.me/article/2015/12/16/1450235651_ecxBqZYP.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">垂泪了西厢月下，默守了楼台一诺，这是谁人的谁的世界，又是谁的谁人的红颜！</span><a href=\"http://www.wmpic.me/56112/5670d7035603a_47\"><img src=\"http://p3.wmpic.me/article/2015/12/16/1450235651_tlhAKGSr.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">我不说，你又何必相问，你要说，又何必是挥手告别，看花曾与楚王醉，听琴又把张生恨</span><a href=\"http://www.wmpic.me/56112/5670d7035c0ef_59\"><img src=\"http://p3.wmpic.me/article/2015/12/16/1450235651_VoRhESVd.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">哪般，这般，是错过还是过错。游园惊梦，牡丹亭下，折柳问飞花，面对春光莫说愁</span><a href=\"http://www.wmpic.me/56112/5670d703620f6_86\"><img src=\"http://p3.wmpic.me/article/2015/12/16/1450235651_vLlpZvCK.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">又是怜惜，又是飞雪，这一季的寒冷，定格了你的出现，一席笺言，一纸窗花，武文又弄墨，没有</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">人能读懂，何时了却这牵挂，这哀怨的感慨！</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('8', '', '期间哪怕对方对待自己如何冷漠，可还是丝毫无法掩盖自我内心澎湃如潮水的爱恋愿意为对方做出很多付出乃至牺牲，可再澎湃的爱恋都会退潮的，一切只是时间的问题终于，当对方逐渐消失在自己的视野中，那份爱也会随着时间和距离而远走只在那段盛夏的青春里留下了一个带着遗憾但承载着爱的身影。那时候的爱恋带着青春里最为执着和青涩的莽撞，觉得为对方哪怕撞得头破血流也无所谓，那些时光，...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56157/56722fa343ca4_18\"><img src=\"http://p3.wmpic.me/article/2015/12/17/1450323875_jElTzJNW.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">期间哪怕对方对待自己如何冷漠，可还是丝毫无法掩盖自我内心澎湃如潮水的爱恋</span><a href=\"http://www.wmpic.me/56157/56722fa375cf2_30\"><img src=\"http://p3.wmpic.me/article/2015/12/17/1450323875_eqLAXwSZ.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">愿意为对方做出很多付出乃至牺牲，可再澎湃的爱恋都会退潮的，一切只是时间的问题</span><a href=\"http://www.wmpic.me/56157/56722fa37ba86_88\"><img src=\"http://p3.wmpic.me/article/2015/12/17/1450323875_gIPzgJeE.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">终于，当对方逐渐消失在自己的视野中，那份爱也会随着时间和距离而远走</span><a href=\"http://www.wmpic.me/56157/56722fa382b20_96\"><img src=\"http://p3.wmpic.me/article/2015/12/17/1450323875_WJOQWcaZ.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">只在那段盛夏的</span>青春<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">里留下了一个带着遗憾但承载着爱的身影。</span><a href=\"http://www.wmpic.me/56157/56722fa388c77_52\"><img src=\"http://p3.wmpic.me/article/2015/12/17/1450323875_dBTdTzxd.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">那时候的爱恋带着青春里最为执着和青涩的莽撞，觉得为对方哪怕撞得头破血流也</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">无所谓，那些时光，就自己而言</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56157/56722fa38ebab_49\"><img src=\"http://p3.wmpic.me/article/2015/12/17/1450323875_vrBrldDe.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">在往后的日子里想起来竟不觉得苦涩，毕竟，那种对爱的劲儿只是年少时才有的</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('9', '', '你象坐在我的对面，裸着背影，好美。我就象蘸在你的秋色里，象画画在你的梦里秋色的嫚影，浪的柔波，就象在我的心里梳洗，那美丽动人的油画你掬一捧秋凉，惊醒了一次美丽的摇晃，我那强悍的体魄，象倒在你梦的黄昏里那如歌的往事在昂首，就象那只盼你而存的花柄，痴望在秋凉里，温饱在爱中。那泊在我窗外的心事，就象细密的雨丝在拉扯着我，我就象在盼着枫叶里的露珠嫣红般的想你，一滴一...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56202/56737da745a52_19\"><img src=\"http://p2.wmpic.me/article/2015/12/18/1450409383_iXmcMoXU.jpg\" alt=\"\" width=\"506\" height=\"375\" style=\"width: 506px; height: 375px;\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">你象坐在我的对面，裸着背影，好美。我就象蘸在你的秋色里，象画画在你的梦里</span><a href=\"http://www.wmpic.me/56202/56737da77ef19_51\"><img src=\"http://p2.wmpic.me/article/2015/12/18/1450409383_ImiLSxNM.jpg\" alt=\"\" width=\"506\" height=\"337\" style=\"width: 506px; height: 337px;\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">秋色的嫚影，浪的柔波，就象在我的心里梳洗，那美丽动人的油画</span><a href=\"http://www.wmpic.me/56202/56737da7c8f94_84\"><img src=\"http://p2.wmpic.me/article/2015/12/18/1450409383_DHHSPFPj.jpg\" alt=\"\" width=\"506\" height=\"339\" style=\"width: 506px; height: 339px;\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">你掬一捧秋凉，惊醒了一次美丽的摇晃，我那强悍的体魄，象倒在你梦的黄昏里</span><a href=\"http://www.wmpic.me/56202/56737da802c2e_22\"><img src=\"http://p2.wmpic.me/article/2015/12/18/1450409383_QdMbRnkK.jpg\" alt=\"\" width=\"508\" height=\"380\" style=\"width: 508px; height: 380px;\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">那如歌的往事在昂首，就象那只盼你而存的花柄，痴望在秋凉里，温饱在爱中。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56202/56737da80b5c1_87\"><img src=\"http://p3.wmpic.me/article/2015/12/18/1450409384_lCOhoeLg.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">那泊在我窗外的心事，就象细密的雨丝在拉扯着我，我就象在盼着枫叶里的露珠</span><a href=\"http://www.wmpic.me/56202/56737da823720_65\"><img src=\"http://p3.wmpic.me/article/2015/12/18/1450409384_AwxNjDyo.jpg\" alt=\"\" width=\"507\" height=\"451\" style=\"width: 507px; height: 451px;\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">嫣红般的想你，一滴一滴的清泪在窗前灼洒，那酸酸的感觉，就象柔入你的美梦中</span><a href=\"http://www.wmpic.me/56202/56737da843de1_96\"><img src=\"http://p3.wmpic.me/article/2015/12/18/1450409384_oSIlvvnJ.jpg\" alt=\"\" width=\"504\" height=\"337\" style=\"width: 504px; height: 337px;\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">红红的相思象在我秋雨咽喉里蠕动，你的魅影逃脱不了我的视线和美丽爱的追踪，</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">一夜的相思，醉红枫叶林，我是你秋色里的嫚影。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('10', '', '从掉下的第一颗眼泪开始，心就碎了。还有什么恋恋不舍不能离开的呢？心在纠结，就象自己到了忘川的岸上，没有了希望的焦点。可恨那时的鲁莽，克制不了自己，才酿成这样的悔悟，叫自己是那么的覆水难收。看到她伤心的样子，就象针扎入他的心口一样的痛。他多么想去安慰她，可是一切都是太迟了，她根本不能接受他的道歉。', '  <p style=\"text-align: center;\"><img src=\"http://p2.wmpic.me/article/2015/12/19/1450500664_punsKoqu.jpg\" alt=\"\"/></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">从掉下的第一颗眼泪开始，心就碎了。还有什么恋恋不舍不能离开的呢？</span><img src=\"http://p2.wmpic.me/article/2015/12/19/1450500664_Yohrmdyk.jpg\" alt=\"\"/></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">心在纠结，就象自己到了忘川的岸上，没有了希望的焦点。</span></p><p style=\"text-align: center;\"><img src=\"http://p2.wmpic.me/article/2015/12/19/1450500664_XPuFUAly.jpg\" alt=\"\"/></p><p style=\"text-align: center;\"><img src=\"http://p2.wmpic.me/article/2015/12/19/1450500664_pdwtHKhb.jpg\" alt=\"\"/><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">可恨那时的鲁莽，克制不了自己，才酿成这样的悔悟，叫自己是那么的覆水难收。</span><img src=\"http://p2.wmpic.me/article/2015/12/19/1450500664_naSjZHoH.jpg\" alt=\"\"/></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">看到她</span>伤心<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">的样子，就象针扎入他的心口一样的痛。</span></p><p style=\"text-align: center;\"><img src=\"http://p2.wmpic.me/article/2015/12/19/1450500664_wFJOhJWb.jpg\" alt=\"\"/></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">他多么想去安慰她，可是一切都是太迟了，她根本不能接受他的道歉。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('11', '', '如果你想好了你想要的，就要心定，安心做好自己身边的事情你就是每天打牌，只要能打成个高手，未必不能在这个社会安身立命柏拉图说，若爱，请深爱，如弃，请彻底，不要曖昧，伤人伤己。柏拉图说，人生最遗憾的，莫过於轻易地放弃了不该放弃的，固执地坚持了不该坚持的。柏拉图说，我以為小鸟飞不过沧海，是因為小鸟没有飞过沧海的勇气，十年以后我才发现，不是小鸟飞不过去，而是沧海的那...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56286/56776d356d94d_39\"><img src=\"http://p2.wmpic.me/article/2015/12/21/1450667317_IHwgqTkU.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">如果你想好了你想要的，就要心定，安心做好自己身边的事情</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56286/56776d35793f8_69\"><img src=\"http://p2.wmpic.me/article/2015/12/21/1450667317_VlxZfxVp.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">你就是每天打牌，只要能打成个高手，未必不能在这个社会安身立命</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56286/56776d358126c_43\"><img src=\"http://p2.wmpic.me/article/2015/12/21/1450667317_ggmAfLiI.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">柏拉图说，若爱，请深爱，如弃，请彻底，不要曖昧，伤人伤己。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56286/56776d3588568_80\"><img src=\"http://p2.wmpic.me/article/2015/12/21/1450667317_dfFVfrqV.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">柏拉图说，</span>人生<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">最遗憾的，莫过於轻易地放弃了不该放弃的，固</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">执地坚持了不该坚持的。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56286/56776d358e6a9_38\"><img src=\"http://p2.wmpic.me/article/2015/12/21/1450667317_fNqevllA.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">柏拉图说，我以為小鸟飞不过沧海，是因為小鸟没有飞过沧海的</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">勇气，十年以后我才发现，不是小鸟飞不过去，而是沧海的那一</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">头，早已没有了等待</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('12', '', '其实爱情里处得好不好，标准就两个字：不累。因为，对的人，不会让你觉得累。当一首歌跟你的某段经历某场感情纠缠在一起的时候，你就再也没法自主地控制情绪。只要这个旋律响起，你都可能会掉眼泪。咖啡馆听到会哭出来，商场里逛街会哭出来，甚至在家里洗澡放歌都可能会掉眼泪。不是忘不掉什么人，只是始终对自己那场无果的付出和被浪费的太炽热的爱耿耿于怀。成熟的人需要面具。戴上，坚...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56339/5678c279052a3_38\"><img src=\"http://p3.wmpic.me/article/2015/12/22/1450754681_kCzOERCX.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">其实</span>爱情<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">里处得好不好，标准就两个字：不累。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56339/5678c27919810_42\"><img src=\"http://p3.wmpic.me/article/2015/12/22/1450754681_ZnYbYGCI.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">因为，对的人，不会让你觉得累。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56339/5678c2791fba5_48\"><img src=\"http://p3.wmpic.me/article/2015/12/22/1450754681_VgaqwBXd.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">当一首歌跟你的某段经历某场</span>感情<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">纠缠在一起的时候，你就再也没法自主地控制情绪。</span><a href=\"http://www.wmpic.me/56339/5678c27925ede_26\"><img src=\"http://p3.wmpic.me/article/2015/12/22/1450754681_fhnRNGzY.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">只要这个旋律响起，你都可能会掉眼泪。咖啡馆听到会哭出来，商场里逛</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">街会哭出来，甚至在家里洗澡放歌都可能会掉眼泪。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56339/5678c2792bd60_40\"><img src=\"http://p3.wmpic.me/article/2015/12/22/1450754681_adhomTaj.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">不是忘不掉什么人，只是始终对自己那场无果的付出和被浪费的太炽热的爱耿耿于怀。</span><a href=\"http://www.wmpic.me/56339/5678c27937367_65\"><img src=\"http://p3.wmpic.me/article/2015/12/22/1450754681_pOhvXNSf.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">成熟的人需要面具。戴上，坚强面对社会；摘下，温柔面对家人。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('13', '', '女人啊，平时要好好护肤，保持身材，多看书，多旅行，有自己的想法去做自己喜欢的事，培养自信。不要把精力全部用在一个男人身上，成天胡思乱想像个怨妇。等你变得更好的时候，你会发现所有的事情都会变得很容易了，包括爱情。真爱你的人会让你活得像自己，不够爱你的人才用改变你的方式来附和自己。有钱的给你物质，有时间的给你陪伴，有情调的给你浪漫，那些都不是爱情。', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56340/5678c3238d394_32\"><img src=\"http://p2.wmpic.me/article/2015/12/22/1450754851_OpwwznTr.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\">女人<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">啊，平时要好好护肤，保持身材，多看书，多旅行，有自己的想法</span><a href=\"http://www.wmpic.me/56340/5678c323a809b_21\"><img src=\"http://p2.wmpic.me/article/2015/12/22/1450754851_soYHOkBt.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">去做自己喜欢的事，培养自信。不要把精力全部用在一个</span>男人<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">身上，成天胡思乱想</span><a href=\"http://www.wmpic.me/56340/5678c323ade8c_83\"><img src=\"http://p2.wmpic.me/article/2015/12/22/1450754851_DfNBqOfq.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">像个怨妇。等你变得更好的时候，你会发现所有的事情都会变得很容易了，包括爱情。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56340/5678c323b4a22_18\"><img src=\"http://p2.wmpic.me/article/2015/12/22/1450754851_pXPhnAWu.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">真爱你的人会让你活得像自己，不够爱你的人才用改变你的方式来附和自己。</span><a href=\"http://www.wmpic.me/56340/5678c323bb4c8_84\"><img src=\"http://p2.wmpic.me/article/2015/12/22/1450754851_QDpSYYer.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">有钱的给你物质，有</span>时间<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">的给你陪伴，有情调的给你浪漫，那些都不是爱情。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('14', '', '可是当你回忆起来的时候是否会不自觉的张扬起笑颜呢？何况，时间从来不会倒流，我们遇到什么样的人，都是自己的命数。几时起，我也会心疼的怀念从前，抹不掉的记忆久久的温故曾经。有时会想，假如不曾与你相遇，青春的回忆里不出现你我怎么会不顾忧伤的撰写成章，憧憬着明天是否依旧美好。若能揭去那肿肿的忧伤独自抱守一个人的清静，纵使寂寞难耐，无须陪伴的温暖，一人苦酒作伴，又何乐...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56369/567901d2d8c41_46\"><img src=\"http://p2.wmpic.me/article/2015/12/22/1450770898_QGdGnVrY.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">可是当你回忆起来的时候是否会不自觉的张扬起笑颜呢？何况，时间从来不会倒流，我们遇到什么</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">样的人，都是自己的命数。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56369/567901d2e00a1_49\"><img src=\"http://p2.wmpic.me/article/2015/12/22/1450770898_cOruUjth.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">几时起，我也会心疼的怀念从前，抹不掉的</span>记忆久久<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">的温故曾经。有时会想，假如不曾与你相遇，</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">青春的回忆里不出现你</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56369/567901d2e7385_32\"><img src=\"http://p2.wmpic.me/article/2015/12/22/1450770898_PTdsRLqU.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">我怎么会不顾忧伤的撰写成章，憧憬着明天是否依旧美好。若能揭去那肿肿的忧伤</span><a href=\"http://www.wmpic.me/56369/567901d2ee68a_91\"><img src=\"http://p2.wmpic.me/article/2015/12/22/1450770898_tyfrEjpm.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">独自抱守</span>一个人<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">的清静，纵使</span>寂寞<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">难耐，无须陪伴的温暖，一人苦酒作伴，又何乐而不为呢？</span><a href=\"http://www.wmpic.me/56369/567901d300909_33\"><img src=\"http://p2.wmpic.me/article/2015/12/22/1450770898_nSoIWLXu.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">一直都想写一篇关于我们的</span>文章<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">，总以为自己很懂你，每当执笔写作，那些言不尽的愁绪</span><a href=\"http://www.wmpic.me/56369/567901d306828_49\"><img src=\"http://p3.wmpic.me/article/2015/12/22/1450770899_hfyuYkpf.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">了了的只道相思。我依旧会去怀念那个让我心疼的女子，娇小的身影，虽然偶尔任</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('15', '', '爱也是一样，在跌倒中爬起，在失败中不气馁，要当一个勇者，不被打败的人。爱是最好的精神财富，爱也是最美丽的催化剂。我们要懂得爱的本质要把爱的美丽发扬光大，你是爱里的人，我也是爱里的人，谁也离不开谁，彼此的心心相印，彼此爱得如胶似漆。我错过了那朵浮云描绘的风情，错过了荷塘里那朵莲盛放的清雅，错过了雨滴敲打柳叶的声韵，也错过了湿了初心的你。当我沉默的面对你时，你怎...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56402/567a1add61f64_53\"><img src=\"http://p3.wmpic.me/article/2015/12/23/1450842845_vQYOmPPY.jpg\" alt=\"\" width=\"507\" height=\"331\" style=\"width: 507px; height: 331px;\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">爱也是一样，在跌倒中爬起，在失败中不气馁，要当一个勇者，不被打败的人。</span><a href=\"http://www.wmpic.me/56402/567a1add8450c_12\"><img src=\"http://p3.wmpic.me/article/2015/12/23/1450842845_oeVPdtIJ.jpg\" alt=\"\" width=\"509\" height=\"332\" style=\"width: 509px; height: 332px;\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">爱是最好的精神财富，爱也是最美丽的催化剂。我们要懂得爱的本质</span><a href=\"http://www.wmpic.me/56402/567a1add8dd40_65\"><img src=\"http://p3.wmpic.me/article/2015/12/23/1450842845_tifPBnYy.jpg\" alt=\"\" width=\"509\" height=\"339\" style=\"width: 509px; height: 339px;\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">要把爱的美丽发扬光大，你是爱里的人，我也是爱里的人，谁也离不开谁，彼此的心</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">心相印，彼此爱得如胶似漆。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56402/567a1add93a4f_92\"><img src=\"http://p3.wmpic.me/article/2015/12/23/1450842845_ZuhNPdPU.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">我错过了那朵浮云描绘的风情，错过了荷塘里那朵莲盛放的清雅，错过了雨滴敲打</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">柳叶的声韵，也错过了湿了初心的你。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56402/567a1add99766_15\"><img src=\"http://p3.wmpic.me/article/2015/12/23/1450842845_Bpxylqhf.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">当我沉默的面对你时，你怎么知道我在心里对你说了多少话呢</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56402/567a1add9fd1c_80\"><img src=\"http://p3.wmpic.me/article/2015/12/23/1450842845_eePuAFwH.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">世界上只有一种</span>生活<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">是关乎自己的那就是“自己的生活”，我们无法改变生活的轨</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">迹，但是还好我们可以在轨迹上留下标记，避免再次来到这里。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('16', '', '而等你我皆成白发初染的老者，你是否还是那个在树下等我的少年？我喜欢在午夜时刻打开自己的邮箱，看着草稿箱保存的那些零零碎碎的文字，默默地欢喜着，忧伤着，回味着，感慨着。温馨的一句提示又来了：夜深了，长发绾君心！没有声音，却是一句无比暖心的关怀犹如那个在远方的人儿，他也许看不见你的身影听不见你的声音，但他总是在凝望，在呼唤。在这样安静的午夜时刻，只适合做一件事情...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56414/567a3faf01947_65\"><img src=\"http://p2.wmpic.me/article/2015/12/23/1450852270_RiUXJTgm.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">而等你我皆成白发初染的老者，你是否还是那个在树下等我的少年？</span><a href=\"http://www.wmpic.me/56414/567a3faf45a71_46\"><img src=\"http://p3.wmpic.me/article/2015/12/23/1450852271_pPeaheNA.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">我喜欢在午夜时刻打开自己的邮箱，看着草稿箱保存的那些零零碎碎的</span>文字<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">，默</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">默地欢喜着，忧伤着，回味着，感慨着。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56414/567a3faf4c805_73\"><img src=\"http://p3.wmpic.me/article/2015/12/23/1450852271_KuMYNXcK.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">温馨的一句提示又来了：夜深了，长发绾君心！没有声音，却是一句无比暖心的关怀</span><a href=\"http://www.wmpic.me/56414/567a3faf53ba8_68\"><img src=\"http://p3.wmpic.me/article/2015/12/23/1450852271_FdTmtJHR.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">犹如那个在远方的人儿，他也许看不见你的身影听不见你的声音，但他总是在凝望，在呼唤。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56414/567a3faf597ba_79\"><img src=\"http://p3.wmpic.me/article/2015/12/23/1450852271_MgASWgNU.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">在这样安静的午夜时刻，只适合做一件事情，想你。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56414/567a3faf63acb_95\"><img src=\"http://p3.wmpic.me/article/2015/12/23/1450852271_bgfMVawX.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">手指飞快地敲打着键盘，将每一分每一秒的相思都说给文字听，每一个汉字就是</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">一个小精灵，翩翩起舞，自由徜徉，它懂我的全部心意，除了它便只有你了。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('17', '', '因为我也不知道我怎么做，我在乎你，我关心你，那是因为我爱你，可是你知道吗？当你每次掛断我电话的时候，我的心就会很失落了、当我知道你在做一些我不想看到的事情的时候，我的心也就凉了、当你不在乎我的感受的时候，我就想哭了。你知道不，当一个人很在乎一个人的时候，你就知道，他是怎么k去害怕失去她，可是，可是这一切对你来说，你在乎吗？你知道吗？每一刻我都很想打你的电话，...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56460/567b67a2d783b_57\"><img src=\"http://p1.wmpic.me/article/2015/12/24/1450928034_PqBnLGOs.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">因为我也不知道我怎么做，我在乎你，我关心你，那是因为</span>我爱你<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">，可是你知道吗？</span><a href=\"http://www.wmpic.me/56460/567b67a2f284b_54\"><img src=\"http://p1.wmpic.me/article/2015/12/24/1450928034_KASDoCCY.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">当你每次掛断我电话的时候，我的心就会很失落了、当我知道你在做一些我不想看到的事情的时候，</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">我的心也就凉了、当你不在乎我的感受的时候，我就想哭了。</span><a href=\"http://www.wmpic.me/56460/567b67a304410_97\"><img src=\"http://p2.wmpic.me/article/2015/12/24/1450928035_yyttUpXR.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">你知道不，当一个人很在乎一个人的时候，你就知道，他是怎么k去害怕失去她，可是，可是这一切</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">对你来说，你在乎吗？</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56460/567b67a30a4d8_72\"><img src=\"http://p2.wmpic.me/article/2015/12/24/1450928035_MCsEDxTo.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">你知道吗？每一刻我都很想打你的电话，听听你的声音。可是当我拨通你的电话，我又害怕，我害</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">怕你在工作，害怕我的电话会打扰你</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56460/567b67a310118_27\"><img src=\"http://p2.wmpic.me/article/2015/12/24/1450928035_lXKZZHUw.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">害怕你不知道我在无时无刻想你，当我想听到你的声音的时候，想知道你在干什么的时候，我却又</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">不想去了解，因为我已经习惯了。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56460/567b67a315fed_65\"><img src=\"http://p2.wmpic.me/article/2015/12/24/1450928035_afHCQPrE.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">害怕了，我的在乎，我的在乎你，你放在心上了吗？对于我的在乎，你能明白吗？</span><a href=\"http://www.wmpic.me/56460/567b67a31be3d_29\"><img src=\"http://p2.wmpic.me/article/2015/12/24/1450928035_CXoNkLsD.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">有多少人都很希望自己的男朋友就像呵护宝贝一样去呵护她自己，有多少人，有多少</span>女人<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">又想去得</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">到这样的一份爱，可是我不知道怎么做才对，属于你心中的男朋友。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('18', '', '细细的雨，淡淡的思绪，随风飘舞。风轻柔地抚摸，心轻柔地安静哪里是彼岸花开？哪里又桃红柳绿？只有听不完的山水清音，说不尽的莫失莫忘，读不尽的情意长天。总是在雨中彳亍，一遍遍问自己，哪里是心的彼岸，哪里是我的家？只有蒹葭水湄，白露苍苍，一帘幽梦。风雨如晦，想你撑着一把花伞，慢慢走过寂寞的长街。想你你的身上暗香浮动，你的眼里风絮飘摇，谁能读懂你眼中的寂寞？清风吹得...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56490/567bb0f22a0f4_74\"><img src=\"http://p1.wmpic.me/article/2015/12/24/1450946802_kFZxqSJs.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">细细的雨，淡淡的思绪，随风飘舞。风轻柔地抚摸，心轻柔地安静</span><a href=\"http://www.wmpic.me/56490/567bb0f243e27_10\"><img src=\"http://p1.wmpic.me/article/2015/12/24/1450946802_wQLaQFGN.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">哪里是彼岸花开？哪里又桃红柳绿？只有听不完的山水清音，说不尽的莫失莫忘，</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">读不尽的情意长天。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56490/567bb0f24b2e0_87\"><img src=\"http://p1.wmpic.me/article/2015/12/24/1450946802_EaSIhtRF.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">总是在雨中彳亍，一遍遍问自己，哪里是心的彼岸，哪里是我的家？</span><a href=\"http://www.wmpic.me/56490/567bb0f250e95_91\"><img src=\"http://p1.wmpic.me/article/2015/12/24/1450946802_lgkECnkz.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">只有蒹葭水湄，白露苍苍，一帘幽梦。风雨如晦，想你撑着一把花伞，慢慢走过寂寞的长街。</span><a href=\"http://www.wmpic.me/56490/567bb0f25704e_17\"><img src=\"http://p1.wmpic.me/article/2015/12/24/1450946802_TvEBpIGR.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">想你你的身上暗香浮动，你的眼里风絮飘摇，谁能读懂你眼中的寂寞？</span><a href=\"http://www.wmpic.me/56490/567bb0f25c6fe_69\"><img src=\"http://p1.wmpic.me/article/2015/12/24/1450946802_JVPXciEx.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">清风吹得去云烟，却吹不散眉弯。曾经红尘陌上，一眸惊鸿，慈悲深种。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('19', '', '我自私地想将你占有，却不敢用温暖的承诺将你守护，我若能封笔戏江湖，该是多好。　相遇，是因为这世界有会说话的文字。相恋，是因为我的世界里有个能懂我心的你。若我能写就繁华，当许你半世繁华。若我能写出风流，当给你倾城浪漫。若我能写下温暖，当陪你度过寒冬。若我能写入你心，当为你写一世情书。若你懂我的爱在心里，当为你不离不弃的守候。　　', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56520/567cba10e19a0_72\"><img src=\"http://p2.wmpic.me/article/2015/12/25/1451014672_LMvLHcTp.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">我自私地想将你占有，却不敢用温暖的承诺将你守护，我若能封笔戏江湖，该是多好。</span><a href=\"http://www.wmpic.me/56520/567cba1103347_72\"><img src=\"http://p3.wmpic.me/article/2015/12/25/1451014673_AmWkxqGD.jpg\" alt=\"\"/></a><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">　</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">相遇，是因为这世界有会说话的文字。</span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">相恋，是因为我的世界里有个能懂我心的你。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56520/567cba1109206_27\"><img src=\"http://p3.wmpic.me/article/2015/12/25/1451014673_XtLpxeah.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">若我能写就繁华，当许你半世繁华。若我能写出风流，当给你倾城浪漫。</span><a href=\"http://www.wmpic.me/56520/567cba110ebe9_80\"><img src=\"http://p3.wmpic.me/article/2015/12/25/1451014673_EGGGaafv.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">若我能写下温暖，当陪你度过寒冬。若我能写入你心，当为你写一世情书。</span><a href=\"http://www.wmpic.me/56520/567cba1115593_87\"><img src=\"http://p3.wmpic.me/article/2015/12/25/1451014673_aEeXNvXC.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">若你懂我的爱在心里，当为你不离不弃的守候。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">　　</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('20', '', '在没遇到那个人之前，你告诉自己：时光总会把最好的人留在后面，我会好好地、静静地等着我的Mr.Right。是的，你觉得自己能够等的到那个人。但是，现实中呢，你又总是在和一些异性朋友保持着来往，不是那么近，又似乎不远。他可能是你曾经拒绝过的人，他也可能是正在暗恋着你的人，他还可能是你的众多异性闺蜜里的一个，等等。你喜欢他们中的一个或者是几个吗？其实答案你知道的，你并不喜...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56548/567cfd3f8f83a_14\"><img src=\"http://p2.wmpic.me/article/2015/12/25/1451031871_YBLHnlpo.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">在没遇到那个人之前，你告诉自己：时光总会把最好的人留在后面，我会好好地、</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">静静地等着我的Mr.Right。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56548/567cfd3fc0ede_49\"><img src=\"http://p2.wmpic.me/article/2015/12/25/1451031871_aANmTqww.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">是的，你觉得自己能够等的到那个人。但是，现实中呢，你又总是在和一些异性朋</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">友保持着来往，不是那么近，又似乎不远。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56548/567cfd3fd66d4_97\"><img src=\"http://p2.wmpic.me/article/2015/12/25/1451031871_THMCJyLi.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">他可能是你曾经拒绝过的人，他也可能是正在暗恋着你的人，他还可能是你的众多</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">异性闺蜜里的一个，等等。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56548/567cfd3fdfe8e_85\"><img src=\"http://p2.wmpic.me/article/2015/12/25/1451031871_OiEEIzDz.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">你喜欢他们中的一个或者是几个吗？其实答案你知道的，你并不喜欢他们，你只是</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">寂寞了，想要有人陪。我说的没错吧。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56548/567cfd400e9de_74\"><img src=\"http://p3.wmpic.me/article/2015/12/25/1451031872_hYiJXPaO.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">直到遇见那个人。之前你们也有过接触，你大概知道他是喜欢你的。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('21', '', '能陪你一生的人却很少，谁在默默的等待，谁又从未走远，谁能为你一直都在，放在心上的人毕竟不多，爱是要珍惜，缘是要相守。时间在守，红尘在望，谁是真心，谁是真爱，相爱不简单，相守却很难，想要对方所想爱要真心去爱，以情动情，才会最难解难分，以爱所爱，心对心才感实在，爱有所谓的重视从不会忽略，情无所谓的兼程，从不畏风雨，最难求的，是始终同行的人最可贵的，是一直在乎你的...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56580/567e0499b1c72_11\"><img src=\"http://p1.wmpic.me/article/2015/12/26/1451099289_ZhbiUaQH.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">能陪你一生的人却很少，谁在默默的等待，谁又从未走远，谁能为你一直都在，放在心上的人毕竟</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">不多，爱是要珍惜，缘是要相守。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56580/567e0499cb470_17\"><img src=\"http://p1.wmpic.me/article/2015/12/26/1451099289_PVnZiMcn.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">时间在守，红尘在望，谁是真心，谁是真爱，相爱不简单，相守却很难，想要对方所想</span><a href=\"http://www.wmpic.me/56580/567e0499d72e5_98\"><img src=\"http://p1.wmpic.me/article/2015/12/26/1451099289_CiHzZiVB.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">爱要真心去爱，以情动情，才会最难解难分，以爱所爱，心对心才感实在，爱有所谓的重视</span><a href=\"http://www.wmpic.me/56580/567e0499de4a1_51\"><img src=\"http://p1.wmpic.me/article/2015/12/26/1451099289_jlurblly.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">从不会忽略，情无所谓的兼程，从不畏风雨，最难求的，是始终同行的人</span><a href=\"http://www.wmpic.me/56580/567e0499e61b6_35\"><img src=\"http://p1.wmpic.me/article/2015/12/26/1451099289_MNAnJcsQ.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">最可贵的，是一直在乎你的心，能一直陪你到最后的，才是最长情的告白，不要忽视身边看似平凡的拥有</span><a href=\"http://www.wmpic.me/56580/567e0499eec7d_81\"><img src=\"http://p1.wmpic.me/article/2015/12/26/1451099289_unHMHsjx.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">更不要等到失去了，才知道什么是怀念，水深而无声，情真而无语，无声无息的陪伴，值得一生去</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">善待，值得一世去信赖。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('22', '', '足以成就人间一段最美的因缘，一份感情，不在乎付出多少，不必去费心计较，却是纯洁如雪，温婉如玉，这就是唯美的真情。红尘过客，朦朦胧胧，情很真爱很深，几人能在心中一世停留，一路擦肩，一路遗忘纵有一刻的欣然相逢，也只是执手一笑，心清澈明净，情坦坦荡荡，不去惊扰别人的平静不去打乱彼此的安宁，淡淡的思念，只源于心底深处深深的懂得，唯一埋葬不了的就是这唯美的真情悠悠我心...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56607/567e3f07ba06a_36\"><img src=\"http://p1.wmpic.me/article/2015/12/26/1451114247_gYaFQSqc.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">足以成就</span>人间<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">一段最美的因缘，一份感情，不在乎付出多少，不必去费心计较，却</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">是纯洁如</span>雪<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">，温婉如玉，这就是唯美的真情。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56607/567e3f07db126_45\"><img src=\"http://p1.wmpic.me/article/2015/12/26/1451114247_SeDrZQLm.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">红尘过客，朦朦胧胧，情很真爱很深，几人能在心中一世停留，一路擦肩，一路遗忘</span><a href=\"http://www.wmpic.me/56607/567e3f07e28ab_48\"><img src=\"http://p1.wmpic.me/article/2015/12/26/1451114247_BrofGlSj.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">纵有一刻的欣然相逢，也只是执手一笑，心清澈明净，情坦坦荡荡，不去惊扰别人的平静</span><a href=\"http://www.wmpic.me/56607/567e3f07ebddb_19\"><img src=\"http://p1.wmpic.me/article/2015/12/26/1451114247_cxVVUNSg.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">不去打乱彼此的安宁，淡淡的思念，只源于心底深处深深的懂得，唯一埋葬不了的</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">就是这唯美的真情</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56607/567e3f07f35d1_84\"><img src=\"http://p1.wmpic.me/article/2015/12/26/1451114247_WmHtmcyZ.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">悠悠我心悠悠情，心海情深心难平，心生温，情长久，爱呵护，情保鲜</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('23', '', '人，似乎总是想的比做的多。最后导致的结果就是，想做的从来都没有去实现，在做的，也只是敷衍了事。如此，最后自然而然的就是一无所获。一直以来，无论什么事，都渴望能以一种轻松的状态去完成，但往往事与愿违或许，这个世界就没有一件事能够轻易获得的。如果获得了，那一定是具有一颗轻松的心情去坦然面对所有困境的人。但很显然，我不是这样的人，至少现在还不是。在这个世界，每个人...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56640/5680ae54d8097_41\"><img src=\"http://p2.wmpic.me/article/2015/12/28/1451273812_ykdQbWZX.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">人，似乎总是想的比做的多。最后导致的结果就是，想做的从来都没有去实现，在做的，也只是敷</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">衍了事。如此，最后自然而然的就是一无所获。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56640/5680ae55113f6_43\"><img src=\"http://p3.wmpic.me/article/2015/12/28/1451273813_MAkFSccJ.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">一直以来，无论什么事，都渴望能以一种轻松的状态去完成，但往往事与愿违</span><a href=\"http://www.wmpic.me/56640/5680ae55175ad_14\"><img src=\"http://p3.wmpic.me/article/2015/12/28/1451273813_fITtnkMH.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">或许，这个世界就没有一件事能够轻易获得的。如果获得了，那一定是具有一颗轻松的</span>心情<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">去坦然</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">面对所有困境的人。但很显然，我不是这样的人，至少现在还不是。</span><a href=\"http://www.wmpic.me/56640/5680ae551d90d_14\"><img src=\"http://p3.wmpic.me/article/2015/12/28/1451273813_UrvRtVTO.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">在这个世界，每个人都在演绎着不同的</span>故事<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">。纸醉金迷是过，穷困潦倒也是过。</span><a href=\"http://www.wmpic.me/56640/5680ae5523371_80\"><img src=\"http://p3.wmpic.me/article/2015/12/28/1451273813_rpIryhFf.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\">生活<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">就是这样，你羡慕别人的风花</span>雪<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">月，别人羡慕你的自由散漫。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('24', '', '陪你走过你怕冷的冬天，向着一路，走向春天。春天里，我可以在暖日里清扬文字，纵使那些铭记和珍藏有泪水成殇，在春日的暖阳里那可以风干成幸福的过往！我在冬天伸出手，想与你相伴，想温暖你整个冬天，和你一块走向春天，你愿意吗？　人的一生，都会遇到这样那样的人，有人喜欢你，有人讨厌你，有你喜欢的人，有你讨厌的人。在这期间，你会遇到你喜欢的但是不喜欢你的人，你会很痛苦。可...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56691/5681ef2de8d52_91\"><img src=\"http://p2.wmpic.me/article/2015/12/29/1451355949_NJDxBuvN.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">陪你走过你怕冷的冬天，向着一路，走向春天。春天里，我可以在暖日里清扬</span>文字<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">，纵使那些铭记</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">和珍藏有泪水成殇，在春日的暖阳里那可以风干成</span>幸福<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">的过往！</span><a href=\"http://www.wmpic.me/56691/5681ef2e09bf3_82\"><img src=\"http://p3.wmpic.me/article/2015/12/29/1451355950_iWVZAAtU.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">我在冬天伸出手，想与你相伴，想温暖你整个冬天，和你一块走向春天，你愿意吗？</span><a href=\"http://www.wmpic.me/56691/5681ef2e11593_66\"><img src=\"http://p3.wmpic.me/article/2015/12/29/1451355950_UDRZyZPb.jpg\" alt=\"\"/></a><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">　</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">人的一生，都会遇到这样那样的人，有人喜欢你，有人讨厌你，有你喜欢的人，有你讨厌的人。</span><a href=\"http://www.wmpic.me/56691/5681ef2e17db9_46\"><img src=\"http://p3.wmpic.me/article/2015/12/29/1451355950_ufuoExLN.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">在这期间，你会遇到你喜欢的但是不喜欢你的人，你会很痛苦。可这只是过程。</span><a href=\"http://www.wmpic.me/56691/5681ef2e22fd6_60\"><img src=\"http://p3.wmpic.me/article/2015/12/29/1451355950_YEsFQong.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">每个人都会遇到这样的人。相信我，你很好，不喜欢你不是你不好，而是他已经遇到他喜欢的人。</span><a href=\"http://www.wmpic.me/56691/5681ef2e2ad42_99\"><img src=\"http://p3.wmpic.me/article/2015/12/29/1451355950_CCAaXGIo.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">酒消愁只会愁更愁，酒只会让你头痛，烟只会让你得肺病，所以别自暴自弃，别因此厌恶自己。</span><a href=\"http://www.wmpic.me/56691/5681ef2e31720_70\"><img src=\"http://p3.wmpic.me/article/2015/12/29/1451355950_YZTpCNPk.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">宝贝，虽然你老说自己是个傻瓜，可我知道你是个聪明的女子。聪明的女子，要自己爱自己。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('25', '', '对于女人，最大的幸福莫过于心中的他为自己揭开婚礼的面纱，而此刻，女孩得到了他给予的最深的爱情她从他坚定的眼神里看不到半点虚假，对于他所说的话始终深信不疑。她深爱了，深得没有尺度，没有距离没有任何理由能让她去伤害那颗真诚的心。她甚至开始责怪自己，责怪这份本不属于她的爱情，只是，真爱会让很多善意的谎言脆弱而无力。相濡以沫的日子，睿智的男孩又怎会没有知觉，甚至他比...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56702/5681faab1976d_44\"><img src=\"http://p1.wmpic.me/article/2015/12/29/1451358891_CmckBTGV.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">对于</span>女人<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">，最大的幸福莫过于心中的他为自己揭开婚礼的面纱，而此刻，</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">女孩得到了他给予的最深的爱情</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56702/5681faab2de35_66\"><img src=\"http://p1.wmpic.me/article/2015/12/29/1451358891_probHMwt.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">她从他坚定的眼神里看不到半点虚假，对于他所说的话始终深信不疑。</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">她深爱了，深得没有尺度，没有距离</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56702/5681faab375f2_11\"><img src=\"http://p1.wmpic.me/article/2015/12/29/1451358891_VfDICwDL.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">没有任何理由能让她去伤害那颗真诚的心。她甚至开始责怪自己，责怪</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">这份本不属于她的爱情，只是，真爱会让很多善意的谎言脆弱而无力。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56702/5681faab3efd6_10\"><img src=\"http://p1.wmpic.me/article/2015/12/29/1451358891_QzpXHOzS.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">相濡以沫的日子，睿智的男孩又怎会没有知觉，甚至他比自己心爱的人</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">更清楚那一张张病历书的情况。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56702/5681faab45dc5_74\"><img src=\"http://p1.wmpic.me/article/2015/12/29/1451358891_tgpckqUA.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">他没有告诉她，怕她内心纠结，无助，怕她把对自己的珍惜转化成放弃。</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">在男孩心里，她就是上天赐予自己的</span>天使</p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56702/5681faab4bfff_96\"><img src=\"http://p1.wmpic.me/article/2015/12/29/1451358891_UeqWwrVD.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">她的善良，她的美丽，她那对自己永远不变的情义让他义无反顾的想要去</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">照顾她一辈子。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56702/5681faab51edd_13\"><img src=\"http://p1.wmpic.me/article/2015/12/29/1451358891_viwQIkqm.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">那些犹豫，踌躇，他早已流放进风中，随风而散去。</span></p>            ', '', '', '');
INSERT INTO `y_album_content` VALUES ('26', '', '温暖的声音呢喃着款款深情，心便在这种乐声的抚慰中渐渐沉静，日间与客户打交道的点点滴滴，在这个时刻似乎离我极远如斯静夜，清香浅萦,窗帘深垂，午夜的灵魂简单得透明，或被丝丝的心绪牵引着穿梭于幻思与真实之间飘荡、游曳。温和的声线，散漫的鼓点，轻颤的低音，珠落的琴键，以及随意的管声，似红酒般浸润着神经迷蒙或恍惚之间，心便似顺着流水般的旋律随波而淌，隐欲在骨子里的景像...', '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56750/568340ab2b807_29\"><img src=\"http://p3.wmpic.me/article/2015/12/30/1451442347_iePBbGhx.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">温暖的声音呢喃着款款深情，心便在这种乐声的抚慰中渐渐沉静，日间</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">与客户打交道的点点滴滴，在这个时刻似乎离我极远</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56750/568340ab44bf0_23\"><img src=\"http://p3.wmpic.me/article/2015/12/30/1451442347_tCUVfQFX.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">如斯静夜，清香浅萦,窗帘深垂，午夜的灵魂简单得透明，或被丝丝的心</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">绪牵引着穿梭于幻思与真实之间飘荡、游曳。</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/56750/568340ab4bd68_31\"><img src=\"http://p3.wmpic.me/article/2015/12/30/1451442347_fGcamOSu.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">温和的声线，散漫的鼓点，轻颤的低音，珠落的琴键，以及随意的管声，</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">似红酒般浸润着神经</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56750/568340ab52878_38\"><img src=\"http://p3.wmpic.me/article/2015/12/30/1451442347_EXEzROZj.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">迷蒙或恍惚之间，心便似顺着流水般的旋律随波而淌，隐欲在骨子里的</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">景像却越来越清晰。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56750/568340ab58e6b_11\"><img src=\"http://p3.wmpic.me/article/2015/12/30/1451442347_RLmZwkJR.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">几许回想，少许片断，些许意念，不断地在眼前闪现，慢慢地就融成一</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">个旋涡，才发现，那些早已侵蚀心底、渗透生命的感念其实无时不在地</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">潜伏着，那无法挥去的渴望痴恋.....</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/56750/568340ab5f649_69\"><img src=\"http://p3.wmpic.me/article/2015/12/30/1451442347_rstOOPgI.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">夜归的你发出的动静更衬了午夜的静谧，惊觉间，分辨着真实与幻思，</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14.44444465637207px; line-height: 26.99652862548828px; background-color: rgb(255, 255, 255);\">就像醉后的挣扎，意像占据了逻辑的清晰</span></p>            ', '', '', '');
