INSERT INTO `y_model_type` VALUES ('3', 'soft', '软件', 'Soft', 'Y', '最新发布的yiifcms、热门手册、免费下载、建站工具', 'yii，cms，免费，下载，最新，热门，最全，精品，建站，工具，安全，稳定', '提供了web开发人员的建站工具和yiifcms发布版本，供感兴趣的用户下载和使用，详情了解尽在yiifcms。');
INSERT INTO `y_model_type` VALUES ('4', 'video', '视频', 'Video', 'Y', '最新上映的电影、热门视频、热播电视剧、下载视频', '视频，电影，微电影，电视剧，MV，MTV，最新，热门，热播，高清，下载', '聚合了用户最喜爱的视频，尽在yiifcms。');

