INSERT INTO `y_page` VALUES ('comment', '评论审核', '', '<p style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\">对内容的评论或者回复需要审核通过后 才可以看到</span> \r\n</p>\r\n<span style=\"font-size:14px;\"></span>', '', '', '', '', '6', '285', 'Y', '1407120050', '1407120061');
INSERT INTO `y_page` VALUES ('disclaimer', '免责声明', '', '<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<ol>\r\n	<li>\r\n		<span style=\"font-size:16px;\">本站点的所有内容 均来自个人和互联网 不存在版权纠纷</span> \r\n	</li>\r\n	<li>\r\n		<span style=\"font-size:16px;\">对于特殊的评论仅仅代表个人观点，和本站无关</span> \r\n	</li>\r\n	<li>\r\n		<span style=\"font-size:16px;\">如果对于转载的内容侵犯了您的权益，请告知于我<br />\r\n</span> \r\n	</li>\r\n</ol>\r\n<p>\r\n	<br />\r\n</p>', '', '', '', '', '5', '258', 'Y', '1407118521', '1407118583');
INSERT INTO `y_page` VALUES ('feature', '特色介绍', '', '<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<strong><span style=\"font-family:Microsoft YaHei;font-size:16px;\" id=\"free\">开源、免费</span></strong> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\">yiifcms是基于yiiframework开发的内容管理系统，它开源、免费，用户可以自由下载、使用、修改、学习交流。</span> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<hr />\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<strong><span style=\"font-family:Microsoft YaHei;font-size:16px;\" id=\"safe\">安全、高效</span></strong> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\">yiiframework本身是一个很安全的高性能的开源框架，使用它您不用担心安全问题，它会使您的网站运行的很良好。</span> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<hr />\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<strong><span style=\"font-size:16px;font-family:Microsoft YaHei;\" id=\"handy\">简洁、方便</span></strong> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\">yiifcms有简洁、方便的后台管理，用户不需要了解复杂的逻辑关系，就可以很快速的发布内容，优化管理。</span> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<hr />\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<span style=\"font-family:Microsoft YaHei;font-size:16px;\" id=\"stable\">轻松、稳定</span> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<span style=\"font-size:14px;\">yiifcms架构合理、布局优美，是您轻松建站和学习的不二之选。</span> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>\r\n<hr />\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>', '', '', '', '', '2', '605', 'Y', '1322999588', '1407118550');
INSERT INTO `y_page` VALUES ('guide', '新手指南', '', '<br/><ol class=\" list-paddingleft-2\" style=\"list-style-type: decimal;\"><li><p><span style=\"font-size: 16px;\"> 内容：包括精品阅读，精美图集，常用下载，云标签等 用户可以自由浏览内容 无权限限制&nbsp; 仅仅对内容发表评论的时候 需要</span><a style=\"font-size: 16px; text-decoration: underline;\" title=\"用户登录\" target=\"_blank\" href=\"http://www.yiifcms.com/user/login\"><span style=\"font-size: 16px;\">登录</span></a><br/></p></li><li><p><span style=\"font-size: 16px;\">搜索：用户可以在网站顶部搜索关键字（单个关键字、多个关键字用空格隔开） 匹配较优质的最贴近需求的前10条结果展示给用户 另外可以通过标签直接搜索</span></p></li><li><p><span style=\"font-size: 16px;\">登录：用户可以通过本站注册的用户进行登录，也可以通过合作伙伴（腾讯QQ、新浪微博、人人网）进行授权登录，无需填写过多信息，直接可以正常登录</span></p></li><li><p><span style=\"font-size: 16px;\">注册：除了第三方登录会自动注册外，本站也提供新用户注册入口(</span><a style=\"font-size: 16px; text-decoration: underline;\" title=\"注册\" target=\"_blank\" href=\"http://www.yiifcms.com/user/register\"><span style=\"font-size: 16px;\">http://www.yiifcms.com/user/register</span></a><span style=\"font-size: 16px;\">) 详情查看：</span><a style=\"font-size: 16px; text-decoration: underline;\" title=\"注册流程\" target=\"_self\" href=\"http://www.yiifcms.com/page/register\"><span style=\"font-size: 16px;\">注册流程</span></a></p></li><li><p><span style=\"font-size: 16px;\">留言反馈：如果您对本站有任何疑问或者意见建议 欢迎使用给本站</span><a style=\"font-size: 16px; text-decoration: underline;\" title=\"留言反馈\" target=\"_blank\" href=\"http://www.yiifcms.com/question/index\"><span style=\"font-size: 16px;\">留言</span></a><br/></p></li></ol><br/>', '', '', '', '', '3', '985', 'Y', '1379392484', '1410312587');
INSERT INTO `y_page` VALUES ('register', '注册流程', '', '<p><strong><span style=\"font-size: 16px;\">1. 用户需要填写用户名，密码，真实邮箱(作为激活和找回密码唯一工具)</span></strong></p><p><strong><span style=\"font-size: 16px;\"><br/></span></strong></p><p><span style=\"font-size:16px;\"><img alt=\"1.jpg\" src=\"/uploads/images/201409/1410311278946354.jpg\" title=\"1410311278946354.jpg\"/></span></p><p><br/></p><p><strong><span style=\"font-size: 16px;\">2.提交之后，有成功提示：</span></strong></p><p><strong><span style=\"font-size: 16px;\"><br/></span></strong></p><p><img alt=\"2.jpg\" src=\"/uploads/images/201409/1410311338996060.jpg\" title=\"1410311338996060.jpg\"/></p><p><br/></p><p><strong><span style=\"font-size: 16px;\">3. 然后登录到注册的邮箱 查看是否收到(yiifcms官网postmaster@yiifcms.com发送的)激活邮件:</span></strong></p><p><strong><span style=\"font-size: 16px;\"><br/></span></strong></p><p><span style=\"font-size: 16px;\"><img alt=\"3.jpg\" src=\"/uploads/images/201409/1410311446674356.jpg\" title=\"1410311446674356.jpg\"/></span></p><p>&nbsp;<strong><span style=\"font-size: 16px;\">点击“激活链接” ：</span></strong></p><p><strong><span style=\"font-size: 16px;\"><br/></span></strong></p><p><img alt=\"4.jpg\" src=\"/uploads/images/201409/1410311491156850.jpg\" title=\"1410311491156850.jpg\"/></p><p><br/></p><p><br/></p><p><span style=\"font-size: 16px;\"><strong>表示账号已经激活！</strong></span></p><p><strong><br/></strong></p><p><br/></p><p><span style=\"font-size: 16px;\"><strong>4.&nbsp; 如果没收到邮件，可以先登录，在个人中心里面可以再次发送邮件进行激活。</strong></span></p><p><br/><span style=\"font-size: 16px;\"></span></p><p><br/></p>', '', '', '', '', '4', '442', 'Y', '1331877791', '1410311739');

-- ----------------------------
-- Table structure for `y_post`
-- ----------------------------
DROP TABLE IF EXISTS `y_post`;
CREATE TABLE `y_post` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int(10) unsigned NOT NULL DEFAULT '1' COMMENT '作者',
  `title` varchar(255) NOT NULL COMMENT '标题',
  `title_second` varchar(255) NOT NULL DEFAULT '' COMMENT '副标题',
  `title_style` varchar(255) NOT NULL DEFAULT '' COMMENT '标题样式',
  `catalog_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '分类',
  `special_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '专题编号',
  `copy_from` varchar(100) NOT NULL DEFAULT '' COMMENT '来源',
  `copy_url` varchar(255) NOT NULL DEFAULT '' COMMENT '来源url',
  `redirect_url` varchar(255) NOT NULL DEFAULT '' COMMENT '跳转URL',
  `tags` varchar(255) NOT NULL DEFAULT '' COMMENT 'tags',
  `view_count` int(10) unsigned NOT NULL DEFAULT '1' COMMENT '查看次数',
  `commend` enum('Y','N') NOT NULL DEFAULT 'N' COMMENT '推荐',
  `favorite_count` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '收藏数量',
  `attention_count` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '关注次数',
  `top_line` enum('Y','N') NOT NULL DEFAULT 'N' COMMENT '置顶',
  `reply_count` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '评论次数',
  `reply_allow` enum('Y','N') NOT NULL DEFAULT 'Y' COMMENT '允许评论',
  `sort_order` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `status` enum('Y','N') NOT NULL DEFAULT 'Y' COMMENT '是否显示',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',
  `update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '最后更新时间',
  PRIMARY KEY (`id`),
  KEY `tags_index` (`tags`) USING BTREE,
  KEY `view_count` (`view_count`)
) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8 COMMENT='文章表';

