-- ----------------------------
-- Table structure for `y_soft_content`
-- ----------------------------
DROP TABLE IF EXISTS `y_soft_content`;
CREATE TABLE `y_soft_content` (
  `soft_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '软件id',
  `soft_file` varchar(100) NOT NULL DEFAULT '' COMMENT '文件路径',
  `introduce` varchar(200) NOT NULL DEFAULT '' COMMENT '摘要',
  `content` text NOT NULL COMMENT '内容',
  `seo_title` varchar(100) NOT NULL DEFAULT '' COMMENT 'SEO标题',
  `seo_keywords` varchar(100) NOT NULL DEFAULT '' COMMENT 'SEO关键字',
  `seo_description` varchar(200) NOT NULL DEFAULT '' COMMENT 'SEO描述',
  PRIMARY KEY (`soft_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='软件内容表';

-- ----------------------------
-- Records of y_soft_content
-- ----------------------------
INSERT INTO `y_soft_content` VALUES ('1', '', '鼠标连点精灵鼠标连点器是利用鼠标驱动编辑的宏进行点击，连点操作是驱动程序的运行，与鼠标本身的点击无关，不管设置多长时间的点击间隔也不会对鼠标造成损害。1.2.0升级日志1、将窗体置为最前端，修复鼠标连点器停止时不显示窗体的问题。2、修复在“自定义模”式下鼠标连点器启动后，滚动鼠标滚轮可调节显示的点击速度的问题3、调整消息提示框显示尺寸', '  鼠标连点精灵鼠标连点器是利用鼠标驱动编辑的宏进行点击，连点操作是驱动程序的运行，与鼠标本身的点击无关，不管设置多长时间的点击间隔也不会对鼠标造成损害。<br /><br />1.2.0升级日志<br />1、将窗体置为最前端，修复鼠标连点器停止时不显示窗体的问题。<br />2、修复在“自定义模”式下鼠标连点器启动后，滚动鼠标滚轮可调节显示的点击速度的问题<br />3、调整消息提示框显示尺寸   ', '', '', '');
INSERT INTO `y_soft_content` VALUES ('2', '', '爱思助手(原苹果刷机助手)是全球首款集一键查询shsh、一键备份shsh、一键刷机功能于一身的强大利器，你只需轻轻地按下一个按钮便可以完成查询shsh、备份shsh、和刷机，真正做到全自动一键式刷机，绝对是傻瓜式操作，简单易用，当你深陷在繁琐复杂刷机解锁教程中时，爱思助手或许就是你的救星，时髦的你一定不会错过的。爱思助手集诸多功能于一身：刷机、越狱、下载固件、升级基带、降级基...', '  爱思助手(原苹果刷机助手)是全球首款集一键查询shsh、一键备份shsh、一键刷机功能于一身的强大利器，你只需轻轻地按下一个按钮便可以完成查询shsh、备份shsh、和刷机，真正做到全自动一键式刷机，绝对是傻瓜式操作，简单易用，当你深陷在繁琐复杂刷机解锁教程中时，爱思助手或许就是你的救星，时髦的你一定不会错过的。<br /><br />爱思助手集诸多功能于一身：刷机、越狱、下载固件、升级基带、降级基带、备份SHSH、读开机密码、解除iPhone已停用、查看SIM锁状态、查看保修期、玩游戏、装软件、文件管理、通讯录备份还原等。<br /><br />爱思助手软件功能：<br />1. 支持iPhone4、3GS刷任意版本的固件(前提是有对应版本的SHSH备份)<br />2. 支持iPhone4、3GS读取开机密码<br />3. 支持iPhone4、3G、3GS解除设备停用<br />4. 支持所有设备退出恢复模式<br />5. 支持一键查询SHSH、一键备份SHSH、一键刷机、一键越狱<br />6. 自动保基带、保证基带版本不变，免去解锁烦恼<br />7. 云技术：SHSH不用保存于硬盘，存取SHSH全部在云端<br />8.无需开发者账户可以直接升级iOS8系统！方便快捷<br /><br />爱思助手 v5.70更新<br />增加 iOS 9.2.1 刷机、激活、查询SHSH等。<br />   ', '', '', '');
INSERT INTO `y_soft_content` VALUES ('3', '', '原木锯材材积计算器软件集原木计算和锯材计算于一体，本软件适用于检尺长2~10米，检尺径4CM以上和单支/元的计算；锯材计算；软件计算价格最适合做生意者使用。本软件用于计算原木、锯材的材积和价格计算，提高计算效率。锯材计算使用说明：1、 锯材材积计算价格最适合做生意者使用；(因为本软件计算的价值全部是只保留整数即小数点后的数全部去掉)；2、 精确计算：当鼠标点击精确计算后，...', '  原木锯材材积计算器软件集原木计算和锯材计算于一体，本软件适用于检尺长2~10米，检尺径4CM以上和单支/元的计算；锯材计算；软件计算价格最适合做生意者使用。本软件用于计算原木、锯材的材积和价格计算，提高计算效率。<br /><br />锯材计算使用说明：<br />1、 锯材材积计算价格最适合做生意者使用；(因为本软件计算的价值全部是只保留整数即小数点后的数全部去掉)；<br />2、 精确计算：当鼠标点击精确计算后，会出现框圈，精确计算价值保留2位小数点(注：小数点第2位数四舍五入)；<br />3、 运费可自行输入，回车计算结果；(如输入-1000元，软件自动识别减掉)；<br />4、 快捷键：回车键切换下一列，TAB键切换下一行；<br />5、长(M)：锯材的长度，比如：4米长输入4；<br />6、宽(mm)：锯材的宽度，比如：20毫米宽输入20；<br />7、厚(mm)：锯材的厚度，比如：20毫米厚输入20；<br />8、支/捆：每捆打多少支，比如：一捆有10支输入10；<br />9、捆(把)：要计算捆的数量，比如：有2000捆输入2000；<br />10、打开：打开保存过的数据，查看记录；<br />11、保存：保存当前下表框里的数据，便于以后查看；<br />12、打印：打印下表框里的内容，请配备打印机使用；<br />13、当数字被遮挡看不见时，请将鼠标移动到表顶头，鼠标变为左右箭头时，可以改变列的大小；<br />例如：用4米的木料锯成17毫米宽、27毫米厚的锯材，每捆打10支，一共锯了1688捆，锯材每立方的价格为1888元。<br />依次输入 4》回车键》17》回车键》27》回车键》10》回车键》1688》回车键》1888》回车键；即可自动计算出材积和对应的金额。<br />14、列表表格的每一列都可以被隐藏，只要把表格头的列向左拖拉到最小即可，再次调出请点击“设置表格”中的默认列宽或者把鼠标移动到隐藏的列向右拖拉。<br /><br />报表使用说明:<br />首先说明，报表功能只能做同一目录下的所有后缀为Xy的文件，请把您需要制作的报表放在一个目录下。比如做2013年10月份的报表，那么前提是10月份的所有收货单必须在同一个文件夹里，所以您收货的时候保存的单子最好是每个月的收货单都分开放，10月建一个文件夹、11月也建一个文件夹，这样方便以后做报表用。制作调货的报表也如此。<br />1、 点击对应“制作报表”按钮<br />2、 在弹出的对话框中选择您要制作报表所在的文件夹目录；<br />3、 点击“确定”，即可；<br /><br />当数字被遮挡看不见时，请将鼠标移动到表顶头，鼠标变为左右箭头时，可以改变列的大小；<br />列表表格的每一列都可以被隐藏，只要把表格头的列向左拖拉到最小即可，再次调出请点击“设置表格”中的默认列宽或者把鼠标移动到隐藏的列向右拖拉。<br /><br />原木锯材材积计算器V6.2版本更新说明：<br />1、修复某些用户无法启动的问题！<br />   ', '', '', '');
