INSERT INTO `y_soft_content` VALUES ('11', '', 'xy刷机助手是xy苹果助手发布的一款苹果刷机助手，主要功能包括一键刷机、修复白苹果、一键备份还原等，支持iPhone/iPad/iTouch所有机型。', '  xy刷机助手是xy苹果助手发布的一款苹果刷机助手，主要功能包括一键刷机、修复白苹果、一键备份还原等，支持iPhone/iPad/iTouch所有机型。   ', '', '', '');
