-- Records of y_special
-- ----------------------------
INSERT INTO `y_special` VALUES ('1', '专题二', '美国特斯拉电动汽车公司（Tesla Motors）首席执行官艾伦•马斯克（Elon Musk）周二表示，该公司力争在未来三年里推出能够“自动驾驶”的汽车，从而正式加入到拥有谷歌(微博)和多家汽车厂商的无人驾驶汽车市场的争夺之中', 'uploads/thumbs/201403/small_8e9e89a2e6a.png', 'uploads/images/201403/8e9e89a2e6a.png', '', '', '', 'Y', '0', '1379547110', '0');
