DROP TABLE IF EXISTS `y_spider_post_content`;
CREATE TABLE `y_spider_post_content` (
  `list_id` int(10) unsigned NOT NULL COMMENT '列表id',
  `content` text NOT NULL COMMENT '详细内容',
  PRIMARY KEY (`list_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='采集文章内容数据表';

-- ----------------------------
-- Records of y_spider_post_content
-- ----------------------------
INSERT INTO `y_spider_post_content` VALUES ('39', '<p>\r\n	@北京海淀法院：【播放影片《观音山》，<a href=\"http://tech.163.com/company/letv/\">乐视网</a>诉<a href=\"http://tech.163.com/company/youku/\">优酷</a>网侵权】因认为优酷手机端未经允许提供其权属影片《观音山》的在线播放服务，原告乐视网信息技术（北京）股份有限公司以侵害信息网络传播权纠纷为由诉至法院，请求判令被告合一信息技术（北京）有限公司停止侵害、赔偿损失。目前，海淀法院受理了此案。\r\n</p>\r\n<p>\r\n	原告乐视网称，被告侵犯了其信息网络传播权，给原告造成了巨大经济损失。请求法院判令其立刻停止在优酷手机端上传播《观音山》，并立即删除；赔偿乐视网9.5万元，并承担因本案而支出的合理费用共计5000元。\r\n</p>\r\n<p>\r\n	目前此案正在进一步审理中。\r\n</p>\r\n<p class=\"f_center\">\r\n	<img alt=\"乐视网诉优酷网侵权播影片：索赔10万元\" src=\"http://img6.cache.netease.com/tech/2016/5/30/20160530172611e7c57.jpg\" />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"gg200x300\">\r\n	<div>\r\n		<a href=\"http://gb.corp.163.com/gb/legal.html\" class=\"ad_hover_href\"></a> \r\n	</div>\r\n</div>');
INSERT INTO `y_spider_post_content` VALUES ('40', '<p class=\"oti<x>tle\">\r\n	（原标题：无忧英语更新招股文件 每股定价区间为18-20美元）\r\n</p>\r\n<p>\r\n	蓝鲸TMT讯&nbsp;5月30日消息，在线教育平台无忧英语近日发布了更新后的招股文件。文件显示，51Talk将把每股美国存托凭证（ADS）发行价区间定在18-20美元，每股ADS相当于15股普通股，共发行240万股ADS，按照招股定价上先计算，公司融资额将为4800万美元。如承销商行使超额配售权，融资额将达5520万美元。估值将达4.07亿美元。\r\n</p>\r\n<p>\r\n	此外，文件中还显示，51Talk的早期投资人为DCM和红杉将各自认购1500万美元和500万美元，合计2000万美元股票。\r\n</p>\r\n<p>\r\n	在本月中旬，无忧英语向SEC递交的F-1上市申请文件中，暴露了其融资造假的事实。对此，无忧英语承认其C轮融资造假，并表示“D轮融资是在进行C轮融资谈判时，和投资人商定的后续协议，当达到此协议要求时，所触发D轮融资是基于相同框架、相同投资方完成的，相当于是C轮融资的延续。”\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"gg200x300\">\r\n	<div>\r\n		<a href=\"http://gb.corp.163.com/gb/legal.html\" class=\"ad_hover_href\"></a> \r\n	</div>\r\n</div>');

-- ----------------------------
-- Table structure for `y_spider_post_list`
-- ----------------------------
DROP TABLE IF EXISTS `y_spider_post_list`;
CREATE TABLE `y_spider_post_list` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `site_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '站点id',
  `url` varchar(100) NOT NULL DEFAULT '' COMMENT '详情页地址',
  `title` varchar(100) NOT NULL DEFAULT '' COMMENT '标题',
  `status` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '状态 0 - 内容未采集  1-内容已采集 2-数据已导入',
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique_index` (`url`)
) ENGINE=InnoDB AUTO_INCREMENT=91 DEFAULT CHARSET=utf8 COMMENT='采集文章列表数据表';

