INSERT INTO `y_page` SET `id` = 'guide' ,`title` = '新手指南' ,`introduce` = '' ,`content` = '<br/><ol class=\" list-paddingleft-2\" style=\"list-style-type: decimal;\"><li><p><span style=\"font-size: 16px;\"> 内容：包括精品阅读，精美图集，常用下载，云标签等 用户可以自由浏览内容 无权限限制&nbsp; 仅仅对内容发表评论的时候 需要</span><a style=\"font-size: 16px; text-decoration: underline;\" title=\"用户登录\" target=\"_blank\" href=\"http://www.yiifcms.com/user/login\"><span style=\"font-size: 16px;\">登录</span></a><br/></p></li><li><p><span style=\"font-size: 16px;\">搜索：用户可以在网站顶部搜索关键字（单个关键字、多个关键字用空格隔开） 匹配较优质的最贴近需求的前10条结果展示给用户 另外可以通过标签直接搜索</span></p></li><li><p><span style=\"font-size: 16px;\">登录：用户可以通过本站注册的用户进行登录，也可以通过合作伙伴（腾讯QQ、新浪微博、人人网）进行授权登录，无需填写过多信息，直接可以正常登录</span></p></li><li><p><span style=\"font-size: 16px;\">注册：除了第三方登录会自动注册外，本站也提供新用户注册入口(</span><a style=\"font-size: 16px; text-decoration: underline;\" title=\"注册\" target=\"_blank\" href=\"http://www.yiifcms.com/user/register\"><span style=\"font-size: 16px;\">http://www.yiifcms.com/user/register</span></a><span style=\"font-size: 16px;\">) 详情查看：</span><a style=\"font-size: 16px; text-decoration: underline;\" title=\"注册流程\" target=\"_self\" href=\"http://www.yiifcms.com/page/register\"><span style=\"font-size: 16px;\">注册流程</span></a></p></li><li><p><span style=\"font-size: 16px;\">留言反馈：如果您对本站有任何疑问或者意见建议 欢迎使用给本站</span><a style=\"font-size: 16px; text-decoration: underline;\" title=\"留言反馈\" target=\"_blank\" href=\"http://www.yiifcms.com/question/index\"><span style=\"font-size: 16px;\">留言</span></a><br/></p></li></ol><br/>' ,`seo_title` = '' ,`seo_keywords` = '' ,`seo_description` = '' ,`link` = '' ,`sort_order` = '3' ,`view_count` = '993' ,`status` = 'Y' ,`create_time` = '1379392484' ,`update_time` = '1410312587' ;
