INSERT INTO `y_soft_content` SET `soft_id` = '1' ,`soft_file` = '' ,`introduce` = '鼠标连点精灵鼠标连点器是利用鼠标驱动编辑的宏进行点击，连点操作是驱动程序的运行，与鼠标本身的点击无关，不管设置多长时间的点击间隔也不会对鼠标造成损害。1.2.0升级日志1、将窗体置为最前端，修复鼠标连点器停止时不显示窗体的问题。2、修复在“自定义模”式下鼠标连点器启动后，滚动鼠标滚轮可调节显示的点击速度的问题3、调整消息提示框显示尺寸' ,`content` = '  鼠标连点精灵鼠标连点器是利用鼠标驱动编辑的宏进行点击，连点操作是驱动程序的运行，与鼠标本身的点击无关，不管设置多长时间的点击间隔也不会对鼠标造成损害。<br /><br />1.2.0升级日志<br />1、将窗体置为最前端，修复鼠标连点器停止时不显示窗体的问题。<br />2、修复在“自定义模”式下鼠标连点器启动后，滚动鼠标滚轮可调节显示的点击速度的问题<br />3、调整消息提示框显示尺寸   ' ,`seo_title` = '' ,`seo_keywords` = '' ,`seo_description` = '' ;
