INSERT INTO `y_spider_image_content` SET `list_id` = '37' ,`cover_img` = 'upload/spider/201601/35ec2782e06.jpg' ,`cover_img_thumb` = 'upload/spider/201601/small_35ec2782e06.jpg' ,`image_list` = '' ,`content` = '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/57583/5695c61622099_81\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452656150_OWsWNRsr.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">花开嫣然，笑入我心，爱亦深深，念亦深深，千里相牵，梦也甘甜，只愿</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">我的爱可以穿越春夏秋冬，只愿你好我便晴天。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/57583/5695c61628982_50\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452656150_sBnZXCTq.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">也许我也只能这样安慰自己，让自己在</span>文字<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">深处满足相互灵魂的一份牵念！</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">可是为什么我们总是在错误的</span>时间</p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/57583/5695c6162fd98_89\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452656150_UITvvkds.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">让我们相遇在红尘深处，尝受那无尽头的煎熬，是老天的戏弄，还是老天</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">的考验，这样的痴念</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/57583/5695c61636805_98\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452656150_meGyAhhZ.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">像风雨里飘零的两片叶子，默默的相望，却不能拥有。你说心疼我的苦，心</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">疼我的泪和累，你说好好活着吧！</span><br style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; white-space: normal; background-color: rgb(255, 255, 255);\"/><a href=\"http://www.wmpic.me/57583/5695c6163c887_13\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452656150_OiCHtLXV.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">活着，就是生命的精彩，也许上天偶尔会不公平，也许上天偶尔会开个小</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">差，给你的</span>人生<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">关上了门，也关闭了窗，怕什么？</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/57583/5695c61642b5e_77\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452656150_qNTbWGeq.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">不是还给你开了一条缝隙吗？有缝隙，就会有阳光进来，有阳光的地方，</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">就有生命的绽放，坚强的人不会因为挫折而后退</span></p>            ' ;
-----------------------------------mysql_cut_line---------------------------------
INSERT INTO `y_spider_image_content` SET `list_id` = '38' ,`cover_img` = 'upload/spider/201601/d452329902b.jpg' ,`cover_img_thumb` = 'upload/spider/201601/small_d452329902b.jpg' ,`image_list` = '' ,`content` = '  <p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/57559/5695b26f64748_34\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452651119_wbLdFYOv.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">回眸岁月，时光蹉跎了多少</span>思念<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">，在春暖花开的季节，谁把思念伏笔，折</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">叠在字里行间，穿梭的紫燕，摇曳的细柳，可否读懂那心惜相牵？</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/57559/5695b26f79022_92\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452651119_fkdOeobF.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">霓影阑珊，清灯常伴，多少个日夜，素笺默默，缱绻了谁的那滴相思泪？</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">梦落霞，情留芳，酒甌满，拾半盏烟火，爱亦痴痴兮，念亦痴痴兮，愿做</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">佛前千年菩提树，只愿等君一回眸。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/57559/5695b26f7f655_49\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452651119_jtzpFDbT.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">陌上花开，流年浅淡，岁月近无声，我落笔成诗，泼墨为你痴醉，自相遇，</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">一回眸，花开嫣然，笑入我心，爱亦深深，念亦深深，千里相牵，梦也甘甜</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">，只愿我的爱可以穿越春夏秋冬，只愿你好我便晴天。</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/57559/5695b26f854fd_75\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452651119_Zhijhdtu.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">也许我也只能这样安慰自己，让自己在</span>文字<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">深处满足相互灵魂的一份牵念！</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">可是为什么我们总是在错误的</span>时间<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">，让我们相遇在红尘深处</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/57559/5695b26f8bfba_63\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452651119_lTBSeyoM.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">尝受那无尽头的煎熬，是老天的戏弄，还是老天的考验，这样的痴念，像风</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">雨里飘零的两片叶子，默默的相望，却不能拥有。你说心疼我的苦，心疼</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">我的泪和累，你说好好活着吧！</span></p><p style=\"text-align: center;\"><a href=\"http://www.wmpic.me/57559/5695b26f92ce5_21\"><img src=\"http://p3.wmpic.me/article/2016/01/13/1452651119_prvbfWHe.jpg\" alt=\"\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">活着，就是生命的精彩，也许上天偶尔会不公平，也许上天偶尔会开个小</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">差，给你的</span>人生<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14px; line-height: 27px; background-color: rgb(255, 255, 255);\">关上了门，也关闭了窗，怕什么？</span></p>            ' ;
