INSERT INTO `#@__post_content` VALUES ('5', '网易科技讯1月29日消息，据路透社报道，本周四雅虎新闻发言人表示，雅虎计划关闭其在阿根廷及墨西哥的办公室。 公司将通过巴西和弗罗里达的业务团队完成其在拉丁美洲的工作任务。雅虎拒绝透露具体有多少岗位会受到影响，但雅虎表示这些办公室都是小型销售导向的部门。（持文） 更多有态度内容请下载网易新闻', '                 <p class=\"f_center\"><img alt=\"雅虎关闭阿根廷及墨西哥办公室\" src=\"http://img5.cache.netease.com/tech/2016/1/29/20160129043120f14f1_550.jpg\" /><br  />  </p>  <br><div class=\"gg200x300\">  </div><p><a href=\"http://tech.163.com/company/netease/\" title=\"网易\" target=\"_blank\">网易</a>科技讯1月29日消息，据路透社报道，本周四<a href=\"http://tech.163.com/company/yahoo/\">雅虎</a>新闻发言人表示，雅虎计划关闭其在阿根廷及墨西哥的办公室。</p>  <p>公司将通过巴西和弗罗里达的业务团队完成其在拉丁美洲的工作任务。雅虎拒绝透露具体有多少岗位会受到影响，但雅虎表示这些办公室都是小型销售导向的部门。（<font face=\"楷体,楷体_GB2312\">持文</font>）</p>  <a href=\"http://www.163.com/newsapp/\">更多有态度内容请下载网易新闻</a>                                                                                                              ', '', '', '');
