INSERT INTO `#@__soft_content` VALUES ('7', '', 'Offline Explore、FlashGet辅助工具：根据文件名前缀、后缀及中间的数字范围，自动产生HTML，包含所需的全部链接(前缀+数字+后缀)。与FlashGet的“添加成批任务”差不多，不过FlashGet只有一个参数，这个允许同时使用两个参数，适应URL按卷号＋页号组合的情况。', '  Offline Explore、FlashGet辅助工具：根据文件名前缀、后缀及中间的数字范围，自动产生HTML，包含所需的全部链接(前缀+数字+后缀)。与FlashGet的“添加成批任务”差不多，不过FlashGet只有一个参数，这个允许同时使用两个参数，适应URL按卷号＋页号组合的情况。   ', '', '', '');
